  var isHidden;
  switch (state) {
    case "on":
      //isHidden = this.isHide();
      if (this.isOn() === true) {
        return true;
      } else {
        return false;
      }
    // break;
    case "open":

      if (this.isOpen() === true) {
        return true;
      } else {
        return false;
      }
    // break;
    case "off":
      if (this.isOn() === false) {
        return true;
      } else {
        return false;
      }
    //  break;
    case "close":
      if (this.isOpen() === false) {
        return true;
      } else {
        return false;
      }
    // break;
    case "+++":
      return true;
    //  break;
    default:
      // default code block
      return false;
    // break;
  }
